import './App.css';
import React, { useState, useEffect, useTransition } from 'react';
import ListaImagenes from './components/ListaImagenes';
import Header from './components/Header';
import Footer from './components/Footer';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Favoritos from './components/Favoritos';

function App() {

    const [imagenes, setImagenes] = useState([]);
    const [favoritos, setFavorito] = useState([]);

    function agregarFavorito(favorito){
      setFavorito(() => {
        favoritos?.push(favorito);
      })
    }

    const [transicionEnCurso, cambiarVista] = useTransition();
    const [vista, setVista] = useState('Home');

    function seleecionarVista(nuevaVista){
      cambiarVista( () => {
        setVista(nuevaVista)
      });
    }

  return (
    <div>
      <Header
        imagenes={imagenes}
        setImagenes={setImagenes}
        seleccionarVista={seleecionarVista}
        vista={vista}
      ></Header>
      { vista === 'Favoritos' && <Favoritos
                              key = "favoritos"
                                  imagenes={favoritos}/>}
      { vista === 'Home' &&  <ListaImagenes
                              agregarFavorito={agregarFavorito}
                              favoritos={favoritos}
                              key = "id"
                              imagenes={imagenes}/>
      }
      <Footer/>
    </div>
  );
}

export default App;
