import React, { useState } from 'react';
import unliked from '../resources/unliked.png'
import liked from '../resources/unliked.png'
const ListaImagenes = ({imagenes, setFavorito, favoritos}) => {

    const [estrella, cambiarEstrella] = useState(unliked);

    function agregarFavorito(foto){
        if(!existeFavorito(foto.id)){
            setFavorito([...favoritos, foto])
            console.log("agregado")
        }else{
            console.log("Ya tienes esta foto en favoritos")
        }
      }
    function existeFavorito(id){
        favoritos.forEach((favorito, index) => {
            if(favoritos[index].id === id){
                return true
            }
        })
        return false;
    }

    return ( 
        <div className='contenedor'>
            <div className='lista-imagenes thumbnail'>
                {imagenes?.map((foto) => (
                    <div className='tarjeta' key={foto.id}>
                        <img className="imagen" src={foto.urls.thumb} alt={foto.alt_description} />
                        <img onClick={() => {agregarFavorito(foto)}} src={estrella} className='botones'></img>
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default ListaImagenes;
