import React, {useState, useEffect} from 'react';
const Buscador = ({imagenes, setImagenes, paginaActual}) => {

    
    const apiKey = 'SudUi7PHizIEvv-99zhBa4q86SYxr_QA_xy13BhVmbY';
    const paginasTotales = 0;
    const [query, setQuery] = useState('');
    const [imagenesCargadas, setImagenesCargadas] = useState(false);


    useEffect(() => {
        if(query){
            fetchfotosPorQuery();
        }else{
            fetchfotos();
        }
    },[paginaActual]);
    
    const fetchfotosPorQuery = async () => {
        console.log(query);
        fetch(`https://api.unsplash.com/search/photos?query=${query}&page=${paginaActual}`, {
        headers: {
            Authorization: `Client-ID ${apiKey}`
        }
        })
        .then(response =>{ 
            const linkHeader = response.headers.get('Link');
            console.log(linkHeader);
            const totalPagesHeader = response.headers.get('X-Total-Pages');
            paginasTotales = Number(totalPagesHeader);
            response.json().then(response => {
            setImagenes(response.results);
            })
        })
        .catch(error => {
            console.log('Error:', error);
        });
    };

    const fetchfotos = async () => {
        fetch(`https://api.unsplash.com/photos/?count=20&page=${paginaActual}`, {
        headers: {
            Authorization: `Client-ID ${apiKey}`
        }
        })
        .then(response =>{ 
            response.json().then(response => {
            setImagenes(response);
            })
        })
        .catch(error => {
            console.log('Error:', error);
        });
    };

    useEffect(() => {
        if(imagenes === undefined){
            fetchfotos();
        }
    }, []);
    

    const handleInputChange = (event) => {
      const value = event.target.value;
      setQuery(value);
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      const formattedQuery = query.replace(/ /g, '+');
      fetchfotosPorQuery();
      
    };

    return ( 
        <>
            <div>
                <form className="form-inline my-2 my-lg-0 buscador" onSubmit={handleSubmit}>
                    <input className="form-control mr-sm-2" 
                    type="search" 
                    placeholder="Buscar imagenes" 
                    aria-label="Buscar imagenes"
                    value={query}
                    onChange={handleInputChange}/>
                </form>
            </div>
        </>
     );
}
 
export default Buscador;
